#include <ShapefilePluginInfo.h>
#include <avtShapefileFileFormat.h>
#include <avtSTSDFileFormatInterface.h>
#include <avtGenericDatabase.h>

// ****************************************************************************
//  Method:  ShapefileCommonPluginInfo::GetDatabaseType
//
