    str += tmpStr;
    SNPRINTF(tmpStr, 1000, "startY = %g\n", atts->GetStartY());
    str += tmpStr;
    SNPRINTF(tmpStr, 1000, "endY = %g\n", atts->GetEndY());
    str += tmpStr;
