            g = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 1)));
            b = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 2)));
            if(PyTuple_Size(tuple) > 3)
                a = int(PyInt_AS_LONG(PyTuple_GET_ITEM(tuple, 3)));
